mapper.select:
select class_schedule.* from class_schedule,student where class_schedule.studentid=student.id
and student.realname like CONCAT('%',#{studentname},'%')
and (class_schedule.teacherid = #{teachername})
and class_schedule.date >= #{datefrom}
and class_schedule.date <= #{dateto}
order by class_schedule.date DESC

select class_schedule.* from class_schedule
where class_schedule.status !='1'


	and (class_schedule.teacherid = #{teachername})




	 and class_schedule.date  >= #{datefrom}



	 and class_schedule.date <= #{dateto}

order by class_schedule.date DESC

select class_schedule.* from class_schedule
where class_schedule.status !=null


	and class_schedule.studentid = #{studentid}




	 and class_schedule.date  >= #{datefrom}



	 and class_schedule.date <= #{dateto}

order by class_schedule.date DESC
